  when 2
    return num_of_numbers
  when 3
    return check_aude
  when 4
    return check_first_caps
  when 5
    return check_caps
  when 6
    return check_underscore
  when 7
    return sort_alphabetically
  when 8
    return fifty_shortest
  when 9
    return index_epensee
  else
    puts "\n\nMais y a pas de fucking #{menu} dans la fucking liste. Stop messing with me. Fuck."
  end
end


